import React from "react";
import "./App.css";

function App() {
  const [formData, setFormData] = React.useState("");

  function formValue(e) {
    const { value } = e.target;
    setFormData(value);
    // console.log(value);
  }
  function wordCount(text) {
    const wordArray = text.trim().split(" ");
    const filteredWords = wordArray.filter((word) => word !== "");
    //console.log(wordcount);
    return filteredWords.length;
  }
  return (
    <div>
      <h1>How fast do you type?</h1>
      <textarea value={formData} onChange={formValue} />
      <h4>TIme Remaining: 5 seconds</h4>
      {/* we passed in the state into the function on click  */}
      <button onClick={() => wordCount(formData)}>Start Game</button>
      <h1>Word Count: 16</h1>
    </div>
  );
}

export default App;
